mapper.select:
select *,null vid
from curriculum

	
		and gradeId=#{gid}
	
	
		and mid=#{mid}
	

order by num desc,hour

mapper.delete:
delete from curr_section

	cid=#{id} and sid in
	
		#{item.id}

delete from section

	id in (
	select sid
	from curr_section
	where cid in (
	
		#{item}
	
	)
	)

mapper.update:
update curriculum as c set hour=(select sum(hour) from section where id in (select sid from curr_section where cid=c.id) ), num=(select count(0) from section where id in (select sid from curr_section where cid =c.id) ) where id in (
#{item}
)

mapper.select:
select *,#{vid} vid, num-(select num from version_curr where vid=#{vid} and cid=c.id) vnum, (select resthour from version_curr where vid=#{vid} and cid=c.id) resthour from curriculum as c
and id in ( select cid from curr_section where sid not in (select sid from version_sec where vid=#{vid}) )
and gradeId=#{gid}
and mid=#{mid}

select *
from curriculum
where id in (select cid from version_curr where vid=#{vid}) and gradeId=#{gid}  and mid=#{mid}
