mapper.select:
select *
from classroom
where
floor in (
select floor
from grade_floor
where gid=#{clazz.grade.id}
)  and id not in (select roomid from plan where `date`=date(#{day})) and people >= #{clazz.people}

	
		and inclass=true
	
	
		and forstudy=true and inclass=false

select *,(select !ap from plan where `date`=date(#{day}) and roomid =classroom.id limit 1) ap from classroom where id in (select roomid from plan where `date`=date(#{day}) group by roomid having count(0)=1 ) and floor in ( select floor from grade_floor where gid=#{clazz.grade.id}) and people >= #{clazz.people}
and inclass=true
and forstudy=true and inclass=false
